const tg = window.Telegram.WebApp;

export function userTelegram() {

    const onClose = () => {
        tg.close()
      }

      
    const onToggleButton = () => {
      if(tg.MainButton.isVisible) {
        tg.MainButton.hide();
      } else {
        tg.MainButton.show();
      }
      }
return {
    onToggleButton,
    onClose,
    tg, 
    user: tg.initDataUnsafe?.user,
}
}
